mapper.select:
SELECT users_id 
FROM users
WHERE users_id=#{users_id}

mapper.update:
UPDATE users
SET users_name=#{users_name}, users_pwd=#{users_pwd}, users_email=#{users_email}, users_sex=#{users_sex}, users_birthday=#{users_birthday}, users_phoneNum=#{users_phoneNum}, users_addr=#{users_addr}, users_cardInfo=#{users_cardInfo}, users_point=#{users_point}
WHERE users_id=#{users_id}
